const IntegrationsPage: React.FC = () => {
  const [currentTab, setCurrentTab] = React.useState<string>("all");
  
  const filteredIntegrations = integrations.filter((integration) => {
    return currentTab === "all" || integration.category === currentTab;
  });

  const getCategories = () => {
    const categories = ["all", ...Array.from(new Set(integrations.map((integration) => integration.category)))];
    return categories;
  };

  const getCategoryLabel = (category: string) => {
    const labels: Record<string, string> = {
      "all": "All Integrations",
      "communication": "Communication",
      "development": "Development",
      "database": "Databases",
      "automation": "Automation",
      "ai": "AI & ML",
      "finance": "Finance",
      "monitoring": "Monitoring",
    };
    
    return labels[category] || category.charAt(0).toUpperCase() + category.slice(1);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "connected":
        return <CheckCircle2 className="h-5 w-5 text-green-500" />;
      case "disconnected":
        return <XCircle className="h-5 w-5 text-gray-400" />;
      case "error":
        return <AlertTriangle className="h-5 w-5 text-red-500" />;
      default:
        return null;
    }
  };

  return (
    <DashboardLayout>
      <div className="flex flex-col gap-6">
        <div className="flex justify-between items-center">
          <div>
            <h2 className="text-xl font-semibold">Integrations</h2>
            <p className="text-sm text-muted-foreground mt-1">
              Connect your platform to various services and tools
            </p>
          </div>
          <Button>
            <PlusCircle className="h-4 w-4 mr-2" />
            New Integration
          </Button>
        </div>
        
        <Tabs value={currentTab} onValueChange={setCurrentTab}>
          <TabsList className="mb-6">
            {getCategories().map((category) => (
              <TabsTrigger key={category} value={category}>
                {getCategoryLabel(category)}
              </TabsTrigger>
            ))}
          </TabsList>
          
          {getCategories().map((category) => (
            <TabsContent key={category} value={category}>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredIntegrations.map((integration) => (
                  <div
                    key={integration.id}
                    className="rounded-lg border bg-card text-card-foreground shadow-sm p-6 hover:border-primary/50 transition-all"
                  >
                    <div className="flex justify-between items-start mb-4">
                      <div className="flex items-center gap-4">
                        <div className="rounded-md bg-muted p-2 flex items-center justify-center">
                          {integration.icon}
                        </div>
                        <div>
                          <h3 className="font-medium text-lg">{integration.name}</h3>
                          <p className="text-sm text-muted-foreground">
                            {integration.description}
                          </p>
                        </div>
                      </div>
                      {getStatusIcon(integration.status)}
                    </div>
                    <div className="space-y-2">
                      <div className="flex justify-between items-center text-sm">
                        <span className="text-muted-foreground">Status</span>
                        <span className={cn(
                          "font-medium",
                          integration.status === "connected" && "text-green-500",
                          integration.status === "disconnected" && "text-gray-400",
                          integration.status === "error" && "text-red-500"
                        )}>
                          {integration.status.charAt(0).toUpperCase() + integration.status.slice(1)}
                        </span>
                      </div>
                      
                      <Button 
                        variant="outline" 
                        className="w-full"
                      >
                        {integration.status === "connected" ? "Configure" : "Connect"}
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </TabsContent>
          ))}
        </Tabs>
      </div>
    </DashboardLayout>
  );
};

export default IntegrationsPage;
